<script>
function translator (current_language){
    if (current_language == "spanish"){
        $(".spanish").hide();
        $(".english").show();
        $("#translate").html("Español").val("english");
    }
    if (current_language == "english"){
        $(".english").hide();
        $(".spanish").show();
        $("#translate").html("English").val("spanish");
    }
}
